CREATE EXTENSION IF NOT EXISTS automerge;

CREATE TABLE changes (
    uuid UUID DEFAULT uuidv7() PRIMARY KEY,
    origin_node TEXT,
    table_name TEXT,
    id UUID,
    change BYTEA
);

CREATE TABLE documents (
    id UUID DEFAULT uuidv7() PRIMARY KEY,
    doc automerge.autodoc
);

